package ch01;

public class FloatDouble {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		실수형 자료형
//		Double을 표준형으로 사용
		
//		float 뒤에 f를 붙여줌
		float fNum = 1.2345678F;
		System.out.println(fNum);
		
//		double은 뭐 안붙여도 됨
//		double이 더 정밀함
		double dNum = 1.2345678901234567;
		System.out.println(dNum);
	}

}
