package ch01;

public class PrintJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		숫자
		System.out.println(100);
		System.out.println(3.3);
		System.out.println(100+10);
		System.out.println("=========");
		
//		문자
		System.out.println('a'); // 홑따옴표 - 문자 표기
		System.out.println("abc"); // 쌍따옴표 - 문자열 표기
		System.out.println("banana"); // 쌍따옴표 - 문자열 표기
		System.out.println(100+10); // 숫자+숫자시 숫자로
		System.out.println(100+"10"); // 숫자+문자시 문자합으로 나옴
		System.out.println("100"+"10"); // 이와 같음
		System.out.println("========"); 
		
//		불리언
		System.out.println(true); // 참
		System.out.println(false); // 거짓
		System.out.println("========");
		
		
		
	}

}
