package array;

public class ArrayDoubleData {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		배열 선언
		double[] data = new double[5];
		
//		입력
		data[0]=10.0;
		data[1]=20.0;
		data[2]=30.0;
		
//		출력
		for(double ddata:data) {
			System.out.println(ddata);
		}
//		초기화 하지 않아도 기본적으로 0이 들어가있음
		
		
	}

}
