package ex2;

public class KeyCodeExample {

	public static void main(String[] args) throws Exception{
		// TODO Auto-generated method stub
		int keyCode;
		
		keyCode = System.in.read();
		System.out.println(keyCode);
		
		keyCode = System.in.read();
		System.out.println(keyCode);
		
		keyCode = System.in.read();
		System.out.println(keyCode);
	}

}
